mapper.update:
update company
set name         = #{name,jdbcType=VARCHAR},
    principal_id = #{principal.id,jdbcType=VARCHAR},
    create_date  = #{createDate,jdbcType=TIMESTAMP},
    update_date  = #{updateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, principal_id, create_date, update_date
from company
where id = #{id,jdbcType=VARCHAR}
