mapper.insert:
insert into employee_address (employee_id, zip, address
  )
values (#{employeeId,jdbcType=VARCHAR}, #{zip,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}
  )

mapper.update:
update employee_address
set zip = #{zip,jdbcType=VARCHAR},
  address = #{address,jdbcType=VARCHAR}
where employee_id = #{employeeId,jdbcType=VARCHAR}

mapper.select:
select employee_id, zip, address
from employee_address
where employee_id = #{employeeId,jdbcType=VARCHAR}
